<?phpclass ConnectToDatabase{     static private $_instance = null;                 private function __construct()     {         $conn = mysql_connect('localhost', 'root', '');         $db = mysql_select_db('medicine', $conn);		 	         mysql_query('SET NAMES utf-8');     }     private function __clone()     {     }                 static function getInstance() {        if (self::$_instance == null){            self::$_instance = new ConnectToDatabase();	}	return self::$_instance;     }          public function query($sql)     {         $result = mysql_query($sql);                  return $result;     }}function query($sql){    $config = ConnectToDatabase::getInstance();        $result = $config->query($sql);    return $result;}