<?phpinclude_once('controllers/Controller.php');//// Базовый контроллер сайта.//abstract class C_Base extends Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $active;	protected $userId;	//	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput($var)	{		$this->title = 'Добавить пациента';		$this->content = '';				$this->active = $var;	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('title' => $this->title, 'content' => $this->content, 'active' => $this->active);			$page = $this->Template('v_main.php', $vars);						echo $page;	}	}